import React, {Component} from 'react';
import './App.css';

import SchoolLink from './components/schoolLink/schoolLink';
import SchoolLinkPreview from './components/schoolLinkPreview/schoolLinkPreview';

class App extends Component {
  state = {
    schoolLink: "",
    headline: "",
    imageID: "",
    inHandDate: "",
    SMI: "",
    bodyCopy: "",
    buttonColor: "",
    footerColor: "",
    subhead: "",
    weekdayMonthDate: "",
    startHour: "",
    startAMPM: "",
    endHour: "",
    endAMPM: "",
    location: "",
    footerURL: "",
    monthAndJobNumber: ""
  }
  valueChangeHandler = (event) => {
    const key = event.target.name
    this.setState({
      [key]: event.target.value
    })
    console.log(this.state);
  }
  render(){
    return (
      <div>
        <h1 className="text-center">Hello World!</h1>
        <div className="container">
          <div className="row">
            <div className="col">
              <p className="text-center">
                <SchoolLink 
                  value={this.state.schoolLink}
                  change={this.valueChangeHandler}
                  />
              </p>
              <form>

              </form>
            </div>
            <div className="col">
              <SchoolLinkPreview 
                link={this.state.schoolLink}
              />
            </div>
          </div>
        </div>
      </div>
    );
  } 
}

export default App;
